
tinymce.PluginManager.add('embedscript', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('embedscript', {
        icon: 'task',
        tooltip: 'Insert Embed Code',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Embed Code',
                width: 500,
                height: 400,
                body: [
                    {type: 'label',name:"help-text", text: "Paste your embed code from Intagram, Twitter, Youtube or other media."},
                    {type: 'textbox', multiline: true, name: 'embed_script', style:'height:320px;'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent(e.data.embed_script + "<p><br /></p>");
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('embedscript', {
        text: 'Embed Code',
        context: 'insert',
        icon: 'task',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Embed Code',
                width: 500,
                height: 400,
                body: [
                    {type: 'label',name:"help-text", text: "Paste your embed code from Intagram, Twitter, Youtube or other media."},
                    {type: 'textbox', multiline: true, name: 'embed_script', style:'height:320px;'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent(e.data.embed_script + "<p><br /></p>");
                }
            });
        }
    });
});