tinymce.PluginManager.add("newsletterbox", function (e) {
    function showNewsletterDialog(e) {
        $('#btn-modal-newsletterbox').click();
    }

	e.addButton('newsletterbox', {
		icon: 'envelope',
		tooltip: 'Insert Newsletter Dialog',
		onclick: showNewsletterDialog
	});

	e.addMenuItem('newsletterbox', {
		icon: 'envelope',
        tooltip: 'Insert Newsletter Dialog',
        onclick: showNewsletterDialog,
        prependToContext: true
	});
});